import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import noteService from './services/notes'

const Display = ({note, changeImportance}) => {
  const text = note.important
    ? 'make not important'
    : 'make important'
  return(
    <li> 
      {note.id} - {note.content} -
      <button onClick={changeImportance}> {text}</button>
    </li>

  )
}

const App = () => {
  const [notes, setNotes] = useState([])
  const [newNote, setNewNote] = useState('a new note')
  const [showAll, setShowAll] = useState(true)

  //load all existing notes from the js-server
  const hook = () => {
    console.log('effect')
    noteService
      .getAll()
      .then (initialNotes => {
        console.log('promoise fulfilled')
        setNotes(initialNotes)
      })
  }
  useEffect(hook, [])

  //add new notes
  const addNote = (event) => {
    event.preventDefault()
    const noteObject = {
      content: newNote,
      date: new Date(),
      important: Math.random() < 0.5
    }

    noteService
      .createNote(noteObject)
      .then(returnedNote => {
        console.log(returnedNote)
        setNotes(notes.concat(returnedNote))
        setNewNote('a new note')
      })
  }

  const onChangeHandler = (event) => {
    console.log(event.target.value)
    setNewNote(event.target.value)
  }

  //change the importance of the note
  const changeImportantOf = (id) => {
    console.log('the importance of ', id, 'needs to be changed')

    const note = notes.find(element => element.id === id)
    const changedNote = {...note, important: !note.important}
    noteService
      .updateNote(id, changedNote)
      .then(response => {
        setNotes(notes.map(note => 
          note.id != id
            ? note
            : response.data
        ))
      })
  }

  console.log('render', notes.length, 'notes')

  return (
    <div>
      <div> 
        <h3> Display existing notes </h3>
        <ul>
          {notes.map(note => 
            <Display key={note.id} note={note} changeImportance={() => changeImportantOf(note.id)}/>
          )}
        </ul>
      </div>
      <div>
      <h3> Adding new notes </h3>
        <form onSubmit={addNote}>
          <input 
            value={newNote}
            onChange={onChangeHandler}
          />
          <button type="submit"> save </button>
        </form>
      </div>
    </div>
  );
}

export default App;
