import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import noteService from './services/notes'


const Button = ({eventHandler, text}) => (
  <button onClick={eventHandler}> {text} </button>
)

const Display = ({note, changeImportance}) => {
  const text = note.important
    ? 'make not important'
    : 'make important'
  return(
    <li className='note'> 
      {note.id} - {note.content} -
      <Button eventHandler={changeImportance}  text={text}/>
    </li>
  )
}

const Notification = ({ message }) => {
  if (message === null) {
    return null
  }
  return (
    <div className="error">
      {message}
    </div>
  )
}

const App = () => {
  const [notes, setNotes] = useState([])
  const [newNote, setNewNote] = useState('a new note')
  const [showAll, setShowAll] = useState(true)
  const [errorMessage, setErrorMessage] = useState('no error happened...')

  //load all existing notes from the js-server
  const hook = () => {
    console.log('effect')
    noteService
      .getAll()
      .then (initialNotes => {
        console.log('promoise fulfilled')
        setNotes(initialNotes)
      })
  }
  useEffect(hook, [])

  //add new notes
  const addNote = (event) => {
    event.preventDefault()
    const noteObject = {
      content: newNote,
      date: new Date(),
      important: Math.random() < 0.5
    }

    noteService
      .createNote(noteObject)
      .then(returnedNote => {
        console.log(returnedNote)
        setNotes(notes.concat(returnedNote))
        setNewNote('a new note')
      })
  }

  const onChangeHandler = (event) => {
    console.log(event.target.value)
    setNewNote(event.target.value)
  }

  //change the importance of the note
  const changeImportantOf = (id) => {
    console.log('the importance of ', id, 'needs to be changed')
    const note = notes.find(n => n.id === id)
    const changedNote = {...note, important: !note.important}
    noteService
      .updateNote(id, changedNote)
      .then(returnedNote => {
        setNotes(notes.map(note => 
          note.id != id
            ? note
            : returnedNote
        ))
      })
      .catch(error => {
        setErrorMessage(`the note '${note.content}' was already deleted from server`)
        setTimeout(() => {
          setErrorMessage(null)
        }, 5000)
        setNotes(notes.filter(n => n.id !== id))
      })
  }

  //show notes with constraint
  const notesToShow = showAll
    ? notes
    : notes.filter(n => n.important === true)

  const displayControl = () => {
      setShowAll(!showAll)
  }

  console.log('render', notes.length, 'notes')

  return (
    <div>
      <div> 
        <h3> Display existing notes </h3>
        <Notification message={errorMessage}/>
        <Button eventHandler={displayControl} text={`Show ${showAll? 'Important' : 'All'}` }/>
        <ul>
          {notesToShow.map(note => 
            <Display key={note.id} note={note} changeImportance={() => changeImportantOf(note.id)}/>
          )}
        </ul>
      </div>
      <div>
      <h3> Adding new notes </h3>
        <form onSubmit={addNote}>
          <input 
            value={newNote}
            onChange={onChangeHandler}
          />
          <button type="submit"> save </button>
        </form>
      </div>
    </div>
  );
}

export default App;
