import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import noteService from './services/notes'


const Button = ({eventHandler, text}) => {
  const buttonStyle = {
    backgroundColor: 'white',
    color: 'black',
    padding: 4,
    margin: 4,
    fontSize: 16,
    cursor: 'pointer',
    fontStyle: 'italic',
    borderRadius: 12
  }
  return <button style={buttonStyle} onClick={eventHandler}> {text} </button>
}


const Display = ({note, changeImportance}) => {
  const text = note.important
    ? 'make not important'
    : 'make important'
  return(
    <li className='note'> 
      {note.id} - {note.content} -
      <Button eventHandler={changeImportance}  text={text}/>
    </li>
  )
}

const Notification = ({ message }) => {
  if (message === null) {
    return null
  }
  return (
    <div className="error">
      {message}
    </div>
  )
}

const App = () => {
  const [products, setProducts] = useState([])
  const [newProductId, setNewProductId] = useState ('a new product id')
  const [showAllProducts, setShowAllProducts] = useState(true)
  const [errorMessage, setErrorMessage] = useState('no error happened...')

  //load all existing products from the js-server
  const hook = () => {
    console.log('effect')
    noteService
      .getAll()
      .then (initialNotes => {
        console.log('promoise fulfilled')
        setProducts(initialNotes)
      })
  }
  useEffect(hook, [])

  //add new product
  const addProduct = (event) => {
    event.preventDefault()
    console.log('button clicked', event.target)
    const productObject = {
      id: newProductId,
      name: "Learning Web Design",
      category: 'Book'
    }

    noteService
      .createNote(productObject)
      .then(returnedProduct => {
        console.log(returnedProduct)
        setProducts(products.concat(returnedProduct))
        setNewProductId('a new product id')
    })
  }

  const handleProductIdChange = (event) => {
    console.log(event.target.value)
    setNewProductId(event.target.value)
  }

  //change the importance of the note
  const changeImportantOf = (id) => {
    //id = 1000
    console.log('the importance of ', id, 'needs to be changed')
    const product = products.find(p => p.id === id)
    const changedProduct = {...product, important: !product.important}
    //const changedNote = {...note}
    noteService
      .updateNote(id, changedProduct)
      .then(returnedProduct => {
        setProducts(products.map(product => 
          product.id !== id
            ? product
            : returnedProduct
        ))
      })
      .catch(error => {
        setErrorMessage('the product was already deleted from server')
        setTimeout(() => {
          setErrorMessage('no errors happened')
        }, 5000)
        setProducts(products.filter(p => p.id !== id))
      })
  }

  //show products with constraint
  const productsToShow = showAllProducts
  ? products
  : products.filter(product => product.category === 'Electronics')

  const displayControl = () => {
      setShowAllProducts(!showAllProducts)
  }

  console.log('render', products.length, 'products')

  return (
    <div>
      <div> 
        <h3> Display existing products </h3>
        <Notification message={errorMessage}/>
        <Button eventHandler={displayControl} text={`Show ${showAllProducts? 'Important' : 'All'}` }/>
        <ul>
          {productsToShow.map(product => 
            <Display key={product.id} product={product}/>
          )}
        </ul>
      </div>
      <div>
      <h3> Adding new products </h3>
      <form onSubmit={addProduct}>
        <input 
        value={newProductId}
        onChange={handleProductIdChange}
        />
        <button type="submit"> Save </button>
      </form>
      </div>
    </div>
  );
}

export default App;
